import numpy as npimport math, sys, osinDir3D = 'outputs/ST_IB_4/sim_0/odds_ratio_'inDir2D = 'outputs/S_IB_4/sim_0/odds_ratio_'thresList = [90, 91, 92, 93, 94, 95, 96, 97, 98, 99, 99.9, 99.99]popSupList = [5, 10, 15, 20, 25, 30, 35, 40, 45]curArr = np.zeros((len(popSupList)*len(thresList), 3), dtype=float)count = 0for i,j in enumerate(popSupList):    print(j)    inArr3D = np.loadtxt(inDir3D + str(j) + ".txt", delimiter=',')    inArr2D = np.loadtxt(inDir2D + str(j) + ".txt", delimiter=',')    inArrStack = np.stack((inArr3D,inArr2D))    for k,l in enumerate(zip(list(inArr3D),list(inArr2D))):        curArr[count][0] = i        curArr[count][1] = k        curArr[count][2] = l[0]-l[1]        count += 1np.savetxt('outputs/odds_ratio_surface.txt', curArr,delimiter=",")