import numpy as npimport math, sys, os#inDir = 'test2/odds_ratio_"inDir = 'outputs/ST_IB_4/sim_0/odds_ratio_'thresList = [90, 91, 92, 93, 94, 95, 96, 97, 98, 99, 99.9, 99.99]popSupList = [5, 10, 15, 20, 25, 30, 35, 40, 45]curArr = np.zeros((len(popSupList)*len(thresList), 3), dtype=float)count = 0for i,j in enumerate(popSupList):    inArr = np.loadtxt(inDir + str(j) + ".txt")    for k,l in enumerate(list(inArr)):        curArr[count][0] = i        curArr[count][1] = k        curArr[count][2] = l        count += 1print(curArr)np.savetxt('outputs/odds_ratio_surface.txt', curArr,delimiter=",")